const form=document.getElementById("contactForm");document.getElementById("source").value="contacto",form.addEventListener("submit",async e=>{e.preventDefault();const t=new FormData(form);try{const e=await fetch(form.action,{method:"POST",body:t,headers:{Accept:"application/json"}}),o=await e.text();let r;try{r=JSON.parse(o)}catch{r=null}r&&r.success?(document.getElementById("modalEnviado").style.display="flex",form.reset()):(console.error("Web3Forms response:",o),document.getElementById("modalError").style.display="flex")}catch(e){console.error(e),document.getElementById("modalError").style.display="flex"}});